/* ---------------------------------------------------------------------------

    Init PubNub and Get your PubNub API Keys:
    http://www.pubnub.com/account#api-keys

--------------------------------------------------------------------------- */

var pubnub = require("./../pubnub.js").init({
    publish_key   : "demo",
    ssl : true,
    subscribe_key : "demo"
});


/* ---------------------------------------------------------------------------
Listen for Messages
--------------------------------------------------------------------------- */

pubnub.subscribe({
  channel : 'foo',
  presence : console.log,
  callback : console.log
});
